import React from "react";

export default function Question({ question }) {
  return (
    <div>
      <h3>{question.text}</h3>
      {question.answer ? (
        <div>
          <button onCl>True</button>
          <button>False</button>
        </div>
      ) : (
        <div>
          <button>True</button>
          <button>False</button>
        </div>
      )}
    </div>
  );
}
