import React from "react";

export default function Question({ question, click }) {
  return (
    <div>
      <h3>{question.text}</h3>
      {
        <div>
          <button style={{color}}onClick={(e) => click(e, question)} value="true">
            True
          </button>
          <button onClick={(e) => click(e, question)} value="false">
            False
          </button>
        </div>
      }
    </div>
  );
}
