import React, { Component } from "react";
import Header from "./components/Header";
import data from "./data";

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      score: 0,
      questions: [],
    };
  }
  componentDidMount() {
    this.newGame();
  }
  render() {
    return (
      <div>
        <Header score={this.state.score} newGame={this.newGame} />
        <Game />
        {/* <Submit /> */}
      </div>
    );
  }
  newGame = () => {
    this.setState({ score: 0, questions: this.get4Q() });
  };
  get4Q = () => {
    const questions = [];
    for (let i = 0; i < 4; i++) {
      let question = this.get1Q();
      while (questions.includes(question)) {
        question = this.get1Q();
      }
      questions.push(question);
    }
    return questions;
  };
  get1Q = () => {
    const index = this.getRandomInt(data.questions.length);
    return data.questions[index];
  };
  getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
  }
}
